void initializeEnemy(CD*,int,int,int);
void createEnemy(CD*,int,int,int);
int getEnemyMoveNumber(CD*,int,int);
void motionEnemy(CD*,SD*,CD*,CD*,ID*);

void initializeEnemy(CD* cd,int variety,int state,int direction){
	createEnemy(cd,variety,getEnemyMoveNumber(cd,variety,state),direction);
	switch(variety){
		case 0:
			cd->a0=-4.5;
			cd->aA=-4.5;
			cd->aX=4.0;
			cd->maxHitPoint=cd->hitPoint=100;
			cd->maxStamina=cd->stamina=100;
			cd->attackPower=20;
			cd->fallFlag=FALSE;
			break;
		default:
			cd->a0=-4.5;
			cd->aA=-4.5;
			cd->aX=2.0;
			cd->maxHitPoint=cd->hitPoint=100;
			cd->maxStamina=cd->stamina=100;
			cd->attackPower=20;
			cd->fallFlag=FALSE;
			break;
	}
	cd->v0=0;
	cd->vA=0;
	cd->vX=0;
	cd->wallKickFlag=FALSE;
	cd->fallTime=0;
	cd->jumpCount=0;
	cd->guardPower=0;
	cd->chargeGauge=0;
	cd->maxChargeGauge=100;
	cd->deathFlag=FALSE;
	cd->existFlag=TRUE;
}
void createEnemy(CD* cd,int variety,int number,int direction){
	int y,x;
	for(y=0;y<CHARACTER_LINES;y++){
		for(x=0;x<CHARACTER_COLS;x++){
			if(direction==RIGHT)
				cd->look[y][x]=enemyInfo[variety][number][y][x];
			else if(direction==LEFT)
				cd->look[y][x]=enemyInfo[variety][number][y][CHARACTER_COLS-x-1];
		}
	}
	cd->variety=variety;
	cd->number=number;
	cd->direction=direction;
}
int getEnemyMoveNumber(CD* cd,int variety,int state){
	switch(variety){
		case 0:
			switch(state){
				case NO_ACTION:
					return 0;
				default:
					return 0;
			}
			break;
		default:
			return 0;
	}
}
void motionEnemy(CD* cd,SD* sd,CD* pd,CD* ed,ID* id){
	switch(cd->motionFlag){
		case NO_ACTION:
			if(cd->fallFlag==FALSE){
				createEnemy(cd,cd->variety,getEnemyMoveNumber(cd,cd->variety,cd->motionFlag),cd->direction);
				cd->vX=0;
			}
			break;
		case WALK:
			createEnemy(cd,cd->variety,getEnemyMoveNumber(cd,cd->variety,cd->motionFlag),cd->direction);
			walk(cd);
			cd->turnFlag=(cd->acceptDirection!=cd->direction)?TRUE:FALSE;
			cd->motionFlag=NO_ACTION;
			break;
		case JUMP:
			if(cd->jumpCount<2){
				if(cd->wallKickFlag==TRUE){
					cd->vX*=-1;
					if(cd->vX>0)
						createEnemy(cd,cd->variety,cd->number,RIGHT);
					else if(cd->vX<0)
						createEnemy(cd,cd->variety,cd->number,LEFT);
				}
				jump(cd);
			}
			cd->turnFlag=(cd->acceptDirection!=cd->direction)?TRUE:FALSE;
			cd->motionFlag=NO_ACTION;
			break;
		default:
			break;
	}
}

