void initEnemyPara(OD* od,int var,int ste,int dir){
	createEnemy(od,var,getEnemyMoveNumber(od,var,ste),dir);
	switch(var){
		case 0:
			od->state.i0=-4.5;
			od->state.iA=-4.5;
			od->state.iX=4.0;
			od->state.maxHP=od->state.HP=100;
			od->state.maxStamina=od->state.stamina=100;
			od->state.fallFlag=FALSE;
			break;
		default:
			od->state.i0=-4.5;
			od->state.iA=-4.5;
			od->state.iX=2.0;
			od->state.maxHP=od->state.HP=100;
			od->state.maxStamina=od->state.stamina=100;
			od->state.fallFlag=FALSE;
			break;
	}
	od->state.v0=0;
	od->state.vA=0;
	od->state.vX=0;
	od->state.wallKickFlag=FALSE;
	od->state.fallTime=0;
	od->state.jumpCount=0;
	od->state.charge=0;
	od->state.maxCharge=100;
	od->state.deathFlag=FALSE;
	od->state.existFlag=TRUE;
}
void createEnemy(OD* od,int var,int num,int dir){
	int y,x;
	if(od->accept.variety!=var||od->accept.number!=num||od->accept.direction!=dir){
		for(y=0;y<OBJECT_LINES;y++){
			for(x=0;x<OBJECT_COLS;x++){
				if(dir>0)
					od->state.look[y][x]=enemyInfo[var][num][y][x];
				else if(dir<0)
					od->state.look[y][x]=enemyInfo[var][num][y][OBJECT_COLS-x-1];
			}
		}
		od->now.variety=var;
		od->now.number=num;
		od->now.direction=dir;
		od->now.existStartY=initInfoE[(dir>0)?0:1][var][num].existStartY;
		od->now.existStartX=initInfoE[(dir>0)?0:1][var][num].existStartX;
		od->now.existEndY=initInfoE[(dir>0)?0:1][var][num].existEndY;
		od->now.existEndX=initInfoE[(dir>0)?0:1][var][num].existEndX;
		od->now.crashStartY=initInfoE[(dir>0)?0:1][var][num].crashStartY;
		od->now.crashStartX=initInfoE[(dir>0)?0:1][var][num].crashStartX;
		od->now.crashEndY=initInfoE[(dir>0)?0:1][var][num].crashEndY;
		od->now.crashEndX=initInfoE[(dir>0)?0:1][var][num].crashEndX;
	}
}
