void initItemPara(OD* od,int var,int ste,int dir){
	createItem(od,var,getItemMoveNumber(od,var,ste),dir);
	switch(var){
		case 0:
			break;
		case 1:
			od->state.eventFlag=FALSE;
			od->state.fallFlag=FALSE;
			od->state.iX=0;
			od->state.i0=-0.1;
			od->state.iA=-0.1;
			od->state.HP=od->state.maxHP=1;
			break;
		default:
			od->state.eventFlag=FALSE;
			od->state.fallFlag=FALSE;
			od->state.iX=0;
			od->state.i0=-1;
			od->state.iA=-1;
			break;
	}
	od->state.v0=0;
	od->state.vA=0;
	od->state.vX=0;
	od->state.fallTime=0;
	od->state.deathFlag=FALSE;
	od->state.existFlag=TRUE;
}
void createItem(OD* od,int var,int num,int dir){
	int y,x;
	if(od->accept.variety!=var||od->accept.number!=num||od->accept.direction!=dir){
		for(y=0;y<OBJECT_LINES;y++){
			for(x=0;x<OBJECT_COLS;x++){
				if(dir>0)
					od->state.look[y][x]=itemInfo[var][num][y][x];
				else if(dir<0)
					od->state.look[y][x]=itemInfo[var][num][y][OBJECT_COLS-x-1];
			}
		}
		od->now.variety=var;
		od->now.number=num;
		od->now.direction=dir;
		od->now.existStartY=initInfoI[(dir>0)?0:1][var][num].existStartY;
		od->now.existStartX=initInfoI[(dir>0)?0:1][var][num].existStartX;
		od->now.existEndY=initInfoI[(dir>0)?0:1][var][num].existEndY;
		od->now.existEndX=initInfoI[(dir>0)?0:1][var][num].existEndX;
		od->now.crashStartY=initInfoI[(dir>0)?0:1][var][num].crashStartY;
		od->now.crashStartX=initInfoI[(dir>0)?0:1][var][num].crashStartX;
		od->now.crashEndY=initInfoI[(dir>0)?0:1][var][num].crashEndY;
		od->now.crashEndX=initInfoI[(dir>0)?0:1][var][num].crashEndX;
	}
}
int getItemMoveNumber(OD* od,int var,int ste){
	switch(var){
		case 1:
			switch(ste){
				case NO_ACTION:
					return 0;
				default:
					return 0;
			}
		default:
			return 0;
	}
}
void motionItem(OD* mine,SD* sd,CD* pd,CD* ed,OD* id){
	switch(mine->now.variety){
		case 1:
			switch(mine->now.motion){
				case NO_ACTION:
					if(mine->state.eventFlag==FALSE){
						if(mine->state.wallHeadFlag==FALSE)
							mine->state.fallFlag=FALSE;
						else{
							mine->state.fallFlag=TRUE;
							mine->state.fallTime=0;
							mine->state.eventFlag=TRUE;
						}
					}
					break;
			}
			break;
	}
}

